INSERT INTO book_tb (isbn13, title, author, publisher, description, category_id, item_id, cover, pub_date, adult, link, lend_status, reservation_status, lend_count, reservation_count)VALUES ('9788932917245', '어린 왕자', '앙투안 드 생텍쥐페리 (지은이), 황현산 (옮긴이)', '열린책들', '전 세계인들의 사랑을 받은 가장 아름다운 이야기, 생텍쥐페리의 &lt;어린 왕자&gt;가 문학 평론가 황현산의 번역으로 열린책들에서 출간되었다. 황현산은 이 작품을 새롭게 번역하면서 생텍쥐페리의 진솔한 문체를 고스란히 살려 내기 위해 심혈을 기울였다.', 2105, 68534943, 'https://image.aladin.co.kr/product/6853/49/cover200/8932917248_2.jpg', '2015-10-20', false, 'http://www.aladin.co.kr/shop/wproduct.aspx?ItemId=68534943&amp;partner=openAPI&amp;start=api', false, false, 0, 0);
INSERT INTO book_tb (isbn13, title, author, publisher, description, category_id, item_id, cover, pub_date, adult, link, lend_status, reservation_status, lend_count, reservation_count)VALUES ('9788937460616', '설국', '가와바타 야스나리 (지은이), 유숙자 (옮긴이)', '민음사', '일본 문학사상 최고의 서정 소설로 평가받는 <설국>이 정식 계약을 통해 출판되었다. 눈 쌓인 온천 마을, 설산, 내연 모를 아름다운 여인, 게이샤 등등 주요 장면이나 인물들의 이미지는 공감각적으로 독자의 감성을 건드린다.', 2105, 328730, 'https://image.aladin.co.kr/product/32/87/cover200/8937460610_3.jpg', '2002-01-28', false, 'http://www.aladin.co.kr/shop/wproduct.aspx?ItemId=328730&amp;partner=openAPI&amp;start=api', false, false, 0, 0);
